SELECT PART_NO AS partNo, PART_TITLE AS partTitle, DEPT_CODE AS deptCode, PART_DATE AS partDate, MEM_NO AS memNo, PART_WRITER AS partWriter, PART_CONTENT AS partContent, PART_HITS AS partHits, PART_DEL_YN AS partDelYN, PART_DEL_DATE AS partDelDate, DEPT_NAME AS deptName FROM ( SELECT ROW_NUMBER() OVER (ORDER BY A.PART_DATE DESC) AS NUM, A.*, D.DEPT_NAME FROM PART_BOARD A LEFT JOIN DEPARTMENT D ON A.DEPT_CODE=D.DEPT_CODE WHERE A.PART_DEL_YN='N' AND A.DEPT_CODE=#{deptCode}
AND (PART_TITLE LIKE #{keyword} OR DBMS_LOB.INSTR(PART_CONTENT, #{keywordOrg} ) > 0) AND (DBMS_LOB.INSTR(${searchType}, #{keywordOrg} ) > 0) AND ${searchType} LIKE #{keyword}
) WHERE NUM BETWEEN #{start} AND #{end}

SELECT A.PART_NO AS partNo, A.PART_TITLE AS partTitle, A.DEPT_CODE AS deptCode, A.PART_DATE AS partDate, A.MEM_NO AS memNo, A.PART_WRITER AS partWriter, A.PART_CONTENT AS partContent, A.PART_HITS AS partHits, A.PART_DEL_YN AS partDelYN, A.PART_DEL_DATE AS partDelDate, D.DEPT_NAME AS deptName, M.MEM_POSITION AS memPosition, M.MEM_PROFILE AS memProfile, F.PART_FILE_NO AS fileNo, F.PART_ORIG_NAME AS origName, F.PART_CHG_NAME AS chgName, F.PART_PATH AS filePath, F.PART_SIZE AS fileSize, F.PART_UPLOAD_DATE AS uploadDate FROM PART_BOARD A LEFT JOIN DEPARTMENT D ON A.DEPT_CODE=D.DEPT_CODE LEFT JOIN MEMBER M ON A.MEM_NO=M.MEM_NO LEFT JOIN PART_FILE F ON A.PART_NO=F.PART_NO WHERE PART_DEL_YN='N'
AND A.DEPT_CODE=#{deptCode}
AND A.PART_NO=#{postNo}

  	SELECT COUNT(*) FROM ${boardType} WHERE 
  	
  		${delType}='N' 
  		PART_NO=${comntPostNo} 
  	
  	
  		AND DEPT_CODE=#{deptCode}
  			
  				
  				
  					AND (${type}TITLE LIKE #{keyword} OR DBMS_LOB.INSTR(${type}CONTENT, #{keywordOrg} ) > 0)
					AND ${searchType} LIKE #{keyword}

  	SELECT MIN(${type}_NO) FROM (
		SELECT 
			ROW_NUMBER() OVER (ORDER BY A.${type}_DATE DESC) AS NUM,
			A.*
		FROM ${boardType} A
		WHERE ${type}_DEL_YN='N' AND DEPT_CODE=#{deptCode}
	) WHERE ${type}_NO>#{postNo}
